function adjustColumnWidthsIfAuto(){for(var t=document.querySelectorAll(".total-columns-auto"),e=0;e<t.length;e++){var n=t[e],o=n.children,d=o.length;if(0!==d)for(var l=n.clientWidth/d,u=0;u<o.length;u++)o[u].style.flex="0 0 "+l+"px"}}document.addEventListener("DOMContentLoaded",adjustColumnWidthsIfAuto),window.addEventListener("resize",adjustColumnWidthsIfAuto);